function App() {
  const [weather, setWeather] = useState(null);

  const API_KEY = "a75ea08d132595950ade930c02765e9c"; 

  const handleSearch = async (city) => {
    const apiUrl = `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${API_KEY}&lang=fr&units=metric`;

    try {
      const response = await fetch(apiUrl);
      const data = await response.json();

      if (data.cod === "404") {
        alert("❌ Ville introuvable !");
        return;
