import React, { useState } from "react";
import styles from "../styles/usestate.module.css"; 

export default function WeatherFormExample() {
  const [weather, setWeather] = useState({
    city: "Tunis",
    temperature: 25,
    condition: "‚òÄÔ∏è Ensoleill√©",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setWeather((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const resetWeather = () => {
    setWeather({
      city: "Tunis",
      temperature: 25,
      condition: "‚òÄÔ∏è Ensoleill√©",
    });
  };
  

  return (
    <div className={styles.container}>
      <h2 className={styles.title}>Modifier la m√©t√©o</h2>

      <form className={styles.form}>
        <input
          type="text"
          name="city"
          value={weather.city}
          onChange={handleChange}
          placeholder="Nom de la ville"
          className={styles.input}
        />

        <input
          type="number"
          name="temperature"
          value={weather.temperature}
          onChange={handleChange}
          placeholder="Temp√©rature (‚ÑÉ)"
          className={styles.input}
        />

        <select
          name="condition"
          value={weather.condition}
          onChange={handleChange}
          className={styles.select}
        >
          <option value="‚òÄÔ∏è Ensoleill√©">‚òÄÔ∏è Ensoleill√©</option>
          <option value="üåßÔ∏è Pluvieux">üåßÔ∏è Pluvieux</option>
          <option value="‚òÅÔ∏è Nuageux">‚òÅÔ∏è Nuageux</option>
          <option value="‚õàÔ∏è Orageux">‚õàÔ∏è Orageux</option>
        </select>

        <button
          type="button"
          onClick={resetWeather}
          className={styles.resetBtn}
        >
          R√©initialiser
        </button>
      </form>

      <div className={styles.card}>
        <h3>Ville : {weather.city}</h3>
        <p>Temp√©rature : {weather.temperature}¬∞C</p>
        <p>Condition : {weather.condition}</p>
      </div>
    </div>
  );
}
